import React, { useState, useEffect } from 'react';
import '../CSS/alert.css';
import '../CSS/styles.css';
import '../CSS/prontos.css';
import '../CSS/proximos.css';
import Proximo from './proximos';
import Pronto from './prontos';

const UserPage = () => {
  const [itens, setItens] = useState([]);


  const getData = async () => {
    try {
      const response = await fetch('http://localhost:3000/fila/list');
      if (!response.ok) throw new Error('Erro ao buscar itens');
      const data = await response.json();
      setItens(data);
    } catch (error) {
      console.error('Erro ao buscar itens:', error);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="App">
      <header>
        <nav className="navigation">
          <img className="seta" src="./img/arrow.png" alt="voltar" />
          <a href="/admin">
            <h1 id="logo">FILA</h1>
          </a>
          <h1 id="rest">Restaurante</h1>
        </nav>
      </header>

      <div className="container-principal">
        <div className="container-prontos">
          <p className="titulo-prontos">Prontos</p>
          <div className="prontos-scroll">
            <ul className="prontos">
              {
                itens.filter(item => item.pronto)
                .map(cadaItem => <Pronto key={cadaItem.id} item={cadaItem} />)
              }
            </ul>
          </div>
        </div>

        <div className="container-proximos">
          <p className="titulo-proximos">Próximos</p>
          <ul className="proximo">
            {
              itens.map(cadaItem => {
                if (cadaItem.pronto) {
                  return <Proximo item={cadaItem}/>
                }
                })
            }
          </ul>
        </div>
      </div>
    </div>
  );
};

export default UserPage;
